import React, { useState,useEffect } from "react";
import APIService  from "./Api_service";
import {useCookies} from "react-cookie";
import ParticlesBg from 'particles-bg';
import {useHistory,Link} from 'react-router-dom';
import { Button } from 'reactstrap';
import '../form.css';
function Login() {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [token, setToken] = useCookies(['mytoken'])
  const [isLogin, setLogin] = useState('true')

  let history = useHistory()
  useEffect(() => {
      if(token['mytoken']){
          history.push('/marketTrack')
      }
  },[token])


    const loginBtn =() =>{
        APIService.LoginUser({username,password})
        .then(resp => setToken('mytoken',resp.token))
        .catch(error => console.log(error))
    }

    const RegisterBtn =() =>{
        APIService.RegisterUser({username,password})
        .then(() => loginBtn())
        .catch(error => console.log(error))

    }

  return (

    <div id='loginform'>
    <ParticlesBg color="#FFFFFF" type="cobweb" bg={true} />
    {isLogin ?  <h2 id='headerTitle'>Login</h2> : <h2 id='headerTitle'>Register</h2>}
      <div className='row11'>
        <label>Username</label>
        <input
          type="text"
          value={username}
          onChange={(e) => setUsername(e.target.value)}


          placeholder="enter username"
        />
      <br/>
        <label>Password</label>
        <input
          type="password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}

          placeholder="enter password"
        />
      <br/>

        {isLogin ? <button onClick={loginBtn}  >
          Login
        </button> : <button onClick={RegisterBtn}  >
          Register
        </button>}

            {isLogin ? <><h5>If you don't have account<Link outline   onClick={()=> setLogin(false)} >SignUp</Link></h5></>: 
                      <><h5>If you have account</h5><Link outline  onClick={()=> setLogin(true)} >Login</Link></>}
                  <br/>
      </div>
    </div>

  );
}

export default Login;
