import React, { useState,useEffect } from "react";
import APIService  from "./Api_service";
import {useCookies} from "react-cookie";
import ParticlesBg from 'particles-bg';
import {useHistory} from 'react-router-dom';
import '../form.css';
function Login() {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [token, setToken] = useCookies(['mytoken'])
  const [isLogin, setLogin] = useState('true')

  let history = useHistory()
  useEffect(() => {
      if(token['mytoken']){
          history.push('/marketTrack')
      }
  },[token])


    const loginBtn =() =>{
        APIService.LoginUser({username,password})
        .then(resp => setToken('mytoken',resp.token))
        .catch(error => console.log(error))
    }

    const RegisterBtn =() =>{
        APIService.RegisterUser({username,password})
        .then(() => loginBtn())
        .catch(error => console.log(error))

    }

  return (

    <div id='loginform'>
    <ParticlesBg color="#FFFFFF" type="cobweb" bg={true} />
    <div >





      <div className='row11'>
      {isLogin ?  <h1>Please Login</h1> : <h1>Please Register</h1>}
        <label  >
          Username
        </label>
        <input

          type="text"
          value={username}
          onChange={(e) => setUsername(e.target.value)}

          id="username"
          placeholder="enter username"
        />
        <br />
        <label htmlFor="Password" className="form=label">
          Password
        </label>
        <input
          type="password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}

          id="password"
          placeholder="enter password"
        />
        <br />
        {isLogin ? <button onClick={loginBtn}  id='button'>
          Login
        </button> : <button onClick={RegisterBtn} >
          Register
        </button>}


        <div >

            {isLogin ? <h5>If you don't have account, Please<button  onClick={()=> setLogin(false)}>Sign Up</button></h5> : 
                      <h5> If you have account ,please Login <button  onClick={()=> setLogin(true)} >Here</button></h5>}
        </div>
      </div>
    </div>
  </div>
  );
}

export default Login;
