import React ,{Component} from 'react';


class StockTable extends Component {
    constructor(props) {
        super(props);

    }
    render() {


        return(

                    <div >

                        <div className="row row-cols-5" >
                                            <div className="col " style={{border: '1px solid',padding:'9px'}} >
                                            <h5 style={{color: 'white' ,textAlign: 'center'}}>Date</h5>
                                            <hr style={{background: "grey"}}/>
                                            {this.props.dateVal.map((item) => {
                                                    return (
                                                    <div style={{color: 'aqua',padding:'9px',fontSize:"1.8vmin"}}>{item}
                                                    <hr style={{background:'grey'}} /></div>

                                                    )
                                                    })
                                                }
                                            </div>

                                            <div className="col " style={{border: '1px solid',padding:'9px',textAlign: 'center'}}>
                                            <h5 style={{color: 'white' ,textAlign: 'center'}}>open</h5>
                                            <hr style={{background: "grey"}}/>
                                            {this.props.openVal.map((item) => {
                                                    return (
                                                    <div style={{color: '#8d93ab',fontSize:"1.8vmin",padding: '9px'}}>{parseFloat(item).toFixed( 2 )}<hr style={{background:'grey'}} /></div>
                                                    )
                                                    })
                                                }
                                            </div>

                                            <div className="col" style={{border: '1px solid',padding:'9px',textAlign: 'center'}}>
                                            <h5 style={{color: 'white' ,textAlign: 'center'}}>High</h5>
                                            <hr style={{background: "grey"}}/>

                                            {this.props.highVal.map((item) => {
                                                    return (
                                                    <div style={{marginBottom:'100',fontSize:"1.8vmin",padding: '9px',color: '#8d93ab'}}>{parseFloat(item).toFixed( 2 )}<hr style={{background:'grey'}} /></div>
                                                    )
                                                    })
                                                }
                                            </div>

                                            <div className="col" style={{border: '1px solid',padding:'9px',textAlign: 'center'}}>
                                            <h5 style={{color: 'white' }}>Low</h5>
                                            <hr style={{background: "grey"}}/>
                                            {this.props.lowVal.map((item) => {
                                                    return (
                                                    <div style={{color: '#8d93ab', margin:'10',fontSize:"1.8vmin",padding: '9px'}}>{parseFloat(item).toFixed( 2 )}<hr style={{background:'grey'}} /></div>
                                                    )
                                                    })
                                                }
                                            </div>

                                            <div className="col" style={{border: '1px solid',padding:'9px'}}>
                                            <h5 style={{color: 'white' ,textAlign: 'center'}}>Close</h5>
                                            <hr style={{background: "grey"}}/>
                                            {this.props.closeVal.map((item) => {
                                                    return (
                                                    <div style={{color: '#8d93ab',fontSize:"1.8vmin",padding: '9px',textAlign: 'center'}}>{parseFloat(item).toFixed( 2 )}<hr style={{background:'grey'}} /></div>
                                                    )
                                                    })
                                                }
                                            </div>
                                        </div>
                                        <div className="container">
            <h1>Simple Inventory Table</h1>
            <table>
                <thead>
                <tr>
                    <th>Product Name</th>
                    <th>Product Category</th>
                    <th>Unit Price</th>
                    <th>Action</th>
                </tr>
                </thead>
                <tbody>
                {
                    data.map((item) => (
                        <tr key={item.id}>
                            <td>{item.product_name}</td>
                            <td>{item.product_category}</td>
                            <td>
                                {
                                    inEditMode.status && inEditMode.rowKey === item.id ? (
                                        <input value={unitPrice}
                                               onChange={(event) => setUnitPrice(event.target.value)}
                                        />
                                    ) : (
                                        item.unit_price
                                    )
                                }
                            </td>
                            <td>
                                {
                                    inEditMode.status && inEditMode.rowKey === item.id ? (
                                        <React.Fragment>
                                            <button
                                                className={"btn-success"}
                                                onClick={() => onSave({id: item.id, newUnitPrice: unitPrice})}
                                            >
                                                Save
                                            </button>

                                            <button
                                                className={"btn-secondary"}
                                                style={{marginLeft: 8}}
                                                onClick={() => onCancel()}
                                            >
                                                Cancel
                                            </button>
                                        </React.Fragment>
                                    ) : (
                                        <button
                                            className={"btn-primary"}
                                            onClick={() => onEdit({id: item.id, currentUnitPrice: item.unit_price})}
                                        >
                                            Edit
                                        </button>
                                    )
                                }
                            </td>
                        </tr>
                    ))
                }
                </tbody>
            </table>
        </div>




                    </div>
        )
    }

}
export default StockTable;
