import React, { useState,useEffect } from "react";
import APIService  from "./Api_service";
import {useCookies} from "react-cookie";
import ParticlesBg from 'particles-bg';
import {useHistory} from 'react-router-dom';

function Login() {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [token, setToken] = useCookies(['mytoken'])
  const [isLogin, setLogin] = useState('true')

  let history = useHistory()
  useEffect(() => {
      if(token['mytoken']){
          history.push('/marketTrack')
      }
  },[token])


    const loginBtn =() =>{
        APIService.LoginUser({username,password})
        .then(resp => setToken('mytoken',resp.token))
        .catch(error => console.log(error))
    }

    const RegisterBtn =() =>{
        APIService.RegisterUser({username,password})
        .then(() => loginBtn())
        .catch(error => console.log(error))

    }

  return (


    <div className="App">
    <ParticlesBg color="#FFFFFF" type="cobweb" bg={true} />
      <br />
      <br />
      {isLogin ?  <h1>Please Login</h1> : <h1>Please Register</h1>}

      <br />
      <br />
      <div className="mb-3">
        <label htmlFor="Username" className="form-label">
          Username
        </label>
        <input
          type="text"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          className="form-control"
          id="username"
          placeholder="enter username"
        />
        <br />
        <label htmlFor="Password" className="form=label">
          Password
        </label>
        <input
          type="password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          className="form-control"
          id="password"
          placeholder="enter password"
        />
        <br />
        {isLogin ? <button onClick={loginBtn} className="btn btn-primary">
          Login
        </button> : <button onClick={RegisterBtn} className="btn btn-primary">
          Register
        </button>}


        <div className= 'mb-3'>
            <br/>
            {isLogin ? <h5>If you don't have account, Please<button className="btn btn-primary btn-sm" onClick={()=> setLogin(false)}>Sign Up</button></h5> : 
                       <h5> If you have account ,please Login <button className="btn btn-primary btn-sm" onClick={()=> setLogin(true)} >Here</button></h5>}
        </div>
      </div>
    </div>

  );
}

export default Login;
