import React from "react";
import  {useState, useEffect} from 'react';
import { useCookies } from "react-cookie"; 
import { useHistory } from "react-router-dom";
import APIService from "../APIService";
function FormValidation(props) {
    const [title, setTitle] = useState('')
    const [desc, setDesc] = useState("")
    const [token] = useCookies(['mytoken'])



    useEffect(()=>{
      setTitle(props.article.title)
      setDesc(props.article.desc)
    },[props.article])



    const updateArticle =()=>{
        APIService.UpdateArticle(props.article.id, {title,desc},token['mytoken'])
        .then(resp => props.updatedInformation(resp))
    }
    const insertArticle = () => {
        APIService.InsertArticle({title,desc}, token['mytoken'])
        .then(resp => props.insertedInformation(resp) )
    }





  return (
    <div>
      {props.article ? (
        <div className="mb-3">
          <label htmlFor="title" className="form-label">
            Title
          </label>
          <input
            type="text"
            className="form-control"
            id="title"
            placeholder="Please enter title"
            value = {title}
            onChange = {e => setTitle(e.target.value)}
          ></input>

          <label htmlFor="desc" className="form-label">
            Description
          </label>
          <textarea className="form-control" id="desc" rows="5" value = {desc}
            onChange = {e => setDesc(e.target.value)}></textarea>
          <br />
          {
            props.article.id ? <button onClick={updateArticle}  className="btn btn-success btn-sm">Update Article</button> :
            <button onClick={insertArticle} className="btn btn-success btn-sm">Insert Article</button>
          }

        </div>
      ) : null}
    </div>
  );
}

export default FormValidation;
